import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './';

const BookedReservations = () => {
    const [reservations, setReservations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchReservations = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/reservations');
                setReservations(response.data);
            } catch (err) {
                setError('Failed to fetch reservations');
            } finally {
                setLoading(false);
            }
        };

        fetchReservations();
    }, []);

    if (loading) return <p>Loading reservations...</p>;
    if (error) return <p className="error">{error}</p>;

    return (
        <div className="booked-reservations">
            <h1>Your Booked Reservations</h1>
            <ul>
                {reservations.map((res, index) => (
                    <li key={index} className="reservation-card">
                        <h2>{res.name}</h2>
                        <p>Email: {res.email}</p>
                        <p>Check-in: {res.checkIn}</p>
                        <p>Check-out: {res.checkOut}</p>
                        <p>Room Type: {res.roomType}</p>
                        <p>Price: ${res.roomPrice}</p>
                        <p>Phone: {res.phone}</p>
                        <p>Message: {res.message || 'No message'}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default BookedReservations;
