import React, { useEffect, useState } from 'react';
import './BookedReservation';

const BookedReservations = () => {
    // Hardcoded user and reservations
    const userReservations = {
        name: "John Doe",
        email: "john.doe@example.com",
        phone: "+1 123 456 7890",
        location: "New York, USA",
        profilePicture: "https://randomuser.me/api/portraits/men/1.jpg",
        reservations: [
            {
                checkIn: "2025-03-25",
                checkOut: "2025-03-30",
                roomType: "Deluxe Suite",
                roomPrice: 250,
                message: "Looking forward to a relaxing stay!"
            },
            {
                checkIn: "2025-06-10",
                checkOut: "2025-06-15",
                roomType: "Standard Room",
                roomPrice: 150,
                message: "Need an extra pillow, please."
            },
            {
                checkIn: "2025-09-05",
                checkOut: "2025-09-10",
                roomType: "Executive Suite",
                roomPrice: 400,
                message: "Anniversary trip, please add some decorations!"
            }
        ]
    };

    const [reservations, setReservations] = useState(null);

    useEffect(() => {
        // Simulating API call delay
        setTimeout(() => {
            setReservations(userReservations);
        }, 1000);
    }, []);

    return (
        <div className="booked-reservations">
            <h1>Your Booked Reservations</h1>
            {reservations === null ? (
                <p>Loading reservations...</p>
            ) : (
                <>
                    {/* User Profile Section */}
                    <div className="user-details">
                        <img
                            src={reservations.profilePicture}
                            alt={`${reservations.name}'s profile`}
                            className="profile-picture"
                        />
                        <div className="user-info">
                            <h2>{reservations.name}</h2>
                            <p>Email: {reservations.email}</p>
                            <p>Phone: {reservations.phone}</p>
                            <p>Location: {reservations.location}</p>
                        </div>
                    </div>

                    {/* Reservations Table */}
                    <table className="reservations-table">
                        <thead>
                            <tr>
                                <th>Check-in</th>
                                <th>Check-out</th>
                                <th>Room Type</th>
                                <th>Price ($)</th>
                                <th>Message</th>
                            </tr>
                        </thead>
                        <tbody>
                            {reservations.reservations.map((res, index) => (
                                <tr key={index}>
                                    <td>{res.checkIn}</td>
                                    <td>{res.checkOut}</td>
                                    <td>{res.roomType}</td>
                                    <td>${res.roomPrice}</td>
                                    <td>{res.message || 'No message'}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </>
            )}
        </div>
    );
};

export default BookedReservations;
