import React, { useState } from 'react';
import axios from 'axios';
import './ReservationPage.css';

const ReservationPage = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        checkIn: '',
        checkOut: '',
        roomType: 'SINGLE',
        roomPrice: 695,
        phone: '',
        message: '',
    });

    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        let updatedData = { ...formData, [name]: value };

        // Auto-update room price based on selection
        if (name === 'roomType') {
            updatedData.roomPrice = value === 'SINGLE' ? 695 : 1295;
        }

        setFormData(updatedData);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            await axios.post('http://localhost:5000/api/reservations', formData);
            alert('Reservation submitted successfully!');
            setFormData({
                name: '',
                email: '',
                checkIn: '',
                checkOut: '',
                roomType: 'SINGLE',
                roomPrice: 695,
                phone: '',
                message: '',
            });
        } catch (error) {
            setError('Error submitting reservation. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="reservation-page">
            <h1>Hotel_Name Reservation Form</h1>
            {error && <p className="error-message">{error}</p>}
            <form onSubmit={handleSubmit}>
                <label>Name:</label>
                <input type="text" name="name" value={formData.name} onChange={handleChange} required />

                <label>Email:</label>
                <input type="email" name="email" value={formData.email} onChange={handleChange} required />

                <label>Check In:</label>
                <input type="date" name="checkIn" value={formData.checkIn} onChange={handleChange} required />

                <label>Check Out:</label>
                <input type="date" name="checkOut" value={formData.checkOut} onChange={handleChange} required />

                <label>Room Type:</label>
                <select name="roomType" value={formData.roomType} onChange={handleChange}>
                    <option value="SINGLE">Single ($695)</option>
                    <option value="DOUBLE">Double ($1295)</option>
                </select>

                <label>Room Price:</label>
                <input type="number" name="roomPrice" value={formData.roomPrice} readOnly />

                <label>Phone:</label>
                <input type="text" name="phone" value={formData.phone} onChange={handleChange} required />

                <label>Message:</label>
                <textarea name="message" value={formData.message} onChange={handleChange}></textarea>

                <button type="submit" disabled={loading}>
                    {loading ? 'Submitting...' : 'Submit Reservation'}
                </button>
                <button 
                    type="button" 
                    className="reset-button"
                    onClick={() => setFormData({
                        name: '',
                        email: '',
                        checkIn: '',
                        checkOut: '',
                        roomType: 'SINGLE',
                        roomPrice: 695,
                        phone: '',
                        message: '',
                    })}
                >
                    Reset All Fields
                </button>
            </form>
        </div>
    );
};

export default ReservationPage;
