import React, { useState } from 'react';
import axios from 'axios';

const ReservationForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        checkIn: '',
        checkOut: '',
        roomType: 'SINGLE',
        roomPrice: 695,
        phone: '',
        message: '',
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/api/reservations', formData);
            alert('Reservation submitted successfully!');
        } catch (error) {
            alert('Error submitting reservation');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>Name:</label>
            <input type="text" name="name" value={formData.name} onChange={handleChange} required />

            <label>Email:</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} required />

            <label>Check In:</label>
            <input type="date" name="checkIn" value={formData.checkIn} onChange={handleChange} required />

            <label>Check Out:</label>
            <input type="date" name="checkOut" value={formData.checkOut} onChange={handleChange} required />

            <label>Room Type:</label>
            <select name="roomType" value={formData.roomType} onChange={handleChange}>
                <option value="SINGLE">Single</option>
                <option value="DOUBLE">Double</option>
            </select>

            <label>Room Price:</label>
            <input type="number" name="roomPrice" value={formData.roomPrice} onChange={handleChange} required />

            <label>Phone:</label>
            <input type="text" name="phone" value={formData.phone} onChange={handleChange} required />

            <label>Message:</label>
            <textarea name="message" value={formData.message} onChange={handleChange}></textarea>

            <button type="submit">Submit Reservation</button>
            <button type="button">Reset All Fields</button>
        </form>
    );
};

export default ReservationForm;
